$(document).ready(function() {
    $(".nav1 ul li").hover(function() {
        $(this).children(".slidDown").stop().fadeToggle()
    });
    $(".nav1_en ul li").hover(function() {
        $(this).children(".slidDown").stop().fadeToggle()
    });
    $(".nav1_en_f ul li").hover(function() {
        $(this).children(".slidDown").stop().fadeToggle()
    });
    $(".item .dec .process .tabT .child").click(function() {
        $(this).addClass("cur").siblings().removeClass("cur");
        $(".tabB_box .tabB").eq($(this).index()).show().siblings().hide()
    });
    $(".item .dec .process .tabB").each(function(a, b) {
        $(this).children(".tabB_t").find("span").click(function() {
            $(this).addClass("cur").siblings().removeClass("cur");
            $(this).parent(".tabB_t").siblings(".tabB_b").find(".list").eq($(this).index()).show().siblings().hide()
        })
    });
    $(".item1 .dec .querys .search .condition div").click(function() {
        $(this).addClass("cur").siblings().removeClass("cur")
    });
    $(".slide_pic").each(function(k, b) {
        var g = 0;
        var j = 0;
        var a = $(this).children(".slide_pic_w");
        var f = $(this).children().find("ul");
        f.children("li:first").clone().appendTo(f);
        var n = f.find("li");
        var o = a.outerWidth(true);
        var d = $(this).find(".left_btn");
        var m = $(this).find(".right_btn");
        var h = $(this).children("ol");
        n.css("width", o);
        f.css("width", n.length * o + 200);
        for (var e = 0; e < n.length - 1; e++) {
            h.append("<li></li>");
            h.children("li:first").addClass("cur")
        }
        var l = h.children("li");
        l.click(function() {
            g = $(this).index();
            j = $(this).index();
            $(this).addClass("cur").siblings().removeClass("cur");
            f.stop().animate({
                left: -g * o
            }, 500)
        });
        m.click(function() {
            c()
        });
        d.click(function() {
            if (g <= 0) {
                f.css("left", -(n.length - 1) * o);
                g = n.length - 1
            }
            g--;
            f.stop().animate({
                left: -g * o
            }, 500);
            j--;
            if (j < 0) {
                j = n.length - 2
            }
            l.eq(j).addClass("cur").siblings().removeClass("cur")
        });

        function c() {
            g++;
            if (g > n.length - 1) {
                g = 1;
                f.css("left", 0)
            }
            f.stop().animate({
                left: -g * o
            }, 500);
            j++;
            if (j > n.length - 2) {
                j = 0
            }
            l.eq(j).addClass("cur").siblings().removeClass("cur")
        }
    });
    $(".nbanner .text .info .dec p").click(function() {
        var a = $(".item .dec").eq($(this).index()).offset().top;
        $("body,html").stop().animate({
            scrollTop: a
        }, 500)
    })
});